<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\RelyingParty\Infrastructure\Driver\Rest;

use Psr\Http\Message\ResponseInterface;
use Psr\Http\Message\ServerRequestInterface;
use OpenApi\Attributes as OA;
use Throwable;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;
use Civi\Lughauth\Shared\Infrastructure\LongTask\Runner;
use Civi\Lughauth\Shared\Infrastructure\LongTask\TaskKey;
use Civi\Lughauth\Shared\Infrastructure\Sql\SqlTemplate;
use Civi\Lughauth\Features\Access\RelyingParty\Application\Usecase\Enable\RelyingPartyEnableUsecase;
use Civi\Lughauth\Features\Access\RelyingParty\Domain\Gateway\RelyingPartyFilter;
use Civi\Lughauth\Features\Access\RelyingParty\Infrastructure\Driver\Batch\RelyingPartyTaskEnable;
use Civi\Lughauth\Features\Access\RelyingParty\Application\Usecase\Enable\RelyingPartyEnableResult;

class RelyingPartyEnableController
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly Runner $runner,
        private readonly SqlTemplate $sql,
        private readonly RelyingPartyEnableUsecase $enableUsecase,
    ) {
    }
    #[OA\Patch(
        path: "/api/access/relying-parties/{uid}/enable",
        tags: ["Relying party"],
        description: "Update the value",
        parameters: [
        new OA\PathParameter(name: "uid", required: true, schema: new OA\Schema(type: "string")),
    ],
        responses: [
        new OA\Response(response: 200, description: "Ok", content: new OA\JsonContent(type: "object", ref:"#/components/schemas/RelyingPartyApiDTO")),
        new OA\Response(response: 404, description: "Not found"),
    ]
    )
    ]
    public function enable(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("enable for Relying party");
        $span = $this->startSpan("enable for Relying party");
        $this->sql->begin();
        try {
            if (!isset($args['uid'])) {
                throw new NotFoundException('-');
            }
            $uid = $args['uid'];
            $result = $this->enableUsecase->enable($uid);
            $this->sql->commit();
            $value = $this->mapRelyingParty($result);
            $response->getBody()->write(json_encode($value));
            return $response->withStatus(201)
                  ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $this->sql->close();
            $span->end();
        }
    }
    public function enableAllForQuery(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("enable all for query Relying party");
        $span = $this->startSpan("enable all for query Relying party");
        try {
            $params = $request->getQueryParams();
            $filter = new RelyingPartyFilter(
                uids: isset($params['uid']) ? [$params['uid']] : (isset($params['uids']) ? explode(',', $params['uids']) : null),
                search: $params['search'] ?? null,
                code: $params['code'] ?? null,
                apiKey: $params['api-key'] ?? null,
            );
            $res = $this->runner->run(RelyingPartyTaskEnable::class, ['filter' => $filter]);
            $response->getBody()->write(json_encode($res));
            return $response;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function checkEnableAllForQueryStatus(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("Check enable all for query Relying party");
        $span = $this->startSpan("Check enable all for query Relying party");
        try {
            if (!isset($args['uid'])) {
                throw new NotFoundException('-');
            }
            $uid = $args['uid'];
            $info = $this->runner->detail(new TaskKey($uid));
            if (!$info) {
                throw new NotFoundException('-');
            }
            $response->getBody()->write(json_encode($info));
            return $response->withStatus(200)
              ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    private function mapRelyingParty(RelyingPartyEnableResult $value): RelyingPartyApiDTO
    {
        $this->logDebug("Map entity to output dto for Relying party");
        $span = $this->startSpan("Map entity to output dto for Relying party");
        try {
            $dto = new RelyingPartyApiDTO();
            $dto->uid = $value->getUid();
            $dto->code = $value->getCode();
            $dto->apiKey = $value->getApiKey();
            $dto->enabled = $value->getEnabled();
            $dto->scopes = $value->getScopes();
            $dto->schemas = $value->getSchemas();
            $dto->policies = $value->getPolicies();
            $dto->version = $value->getVersion();
            return $dto;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
