<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\RelyingParty\Infrastructure\Driver\Rest;

use Psr\Http\Message\ResponseInterface;
use Psr\Http\Message\ServerRequestInterface;
use Throwable;
use Civi\Lughauth\Features\Access\RelyingParty\Domain\Gateway\RelyingPartyFilter;
use Civi\Lughauth\Features\Access\RelyingParty\Domain\Gateway\RelyingPartyCursor;
use Civi\Lughauth\Features\Access\RelyingParty\Domain\RelyingPartyAttributes;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;
use Civi\Lughauth\Features\Access\RelyingParty\Application\Usecase\List\RelyingPartyListUsecase;

class RelyingPartyListController
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly RelyingPartyListUsecase $listUsecase,
        private readonly RelyingPartyRestMapper $mapper,
    ) {
    }
    public function list(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("List Relying party");
        $span = $this->startSpan("List Relying party");
        try {
            $params = $request->getQueryParams();
            $filter = new RelyingPartyFilter(
                uids: isset($params['uid']) ? [$params['uid']] : (isset($params['uids']) ? explode(',', $params['uids']) : null),
                search: $params['search'] ?? null,
                code: $params['code'] ?? null,
                apiKey: $params['api-key'] ?? null,
            );
            $cursor = new RelyingPartyCursor(
                limit: (int)($params['limit'] ?? 100),
                sinceUid: $params['since-uid'] ?? null,
                sinceCode: $params['since-code'] ?? null,
                order: isset($params['order']) ? $this->extractOrder(explode(',', $params['order'])) : null,
            );
            $result = $this->listUsecase->list($filter, $cursor);
            $value = ['items' => array_map(fn ($item) => $this->mapper->mapRelyingParty($item), $result->values())];
            if ($nextLink = $this->nextLink($result->cursor(), $params)) {
                $value['next'] = "?{$nextLink}";
            }
            $response->getBody()->write(json_encode($value));
            return $response->withStatus(200)
              ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function extractOrder(array $order): array
    {
        $this->logDebug("Extract order for Relying party");
        $span = $this->startSpan("Extract order for Relying party");
        try {
            $mapped = [];
            foreach ($order as $ord) {
                if ($ord === 'code-asc') {
                    $mapped[] = 'codeAsc';
                }
                if ($ord === 'code-desc') {
                    $mapped[] = 'codeDesc';
                }
            }
            return $mapped;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function nextLink(?RelyingPartyCursor $value, array $params): ?string
    {
        $this->logDebug("Check generic allow for Relying party");
        $span = $this->startSpan("Generic allow for Relying party");
        try {
            if (!$value) {
                return null;
            }
            $link = [];
            if (isset($params['limit'])) {
                $link['limit'] = 'limit=' . $params['limit'];
            }
            if ($value && $value->sinceUid()) {
                $link['since-uid'] = 'since-uid=' . urlencode($value->sinceUid());
            }
            if ($value && strpos('code-asc', $params['order'] ?? '') !== false && $value->sinceCode()) {
                $link['since-code'] = 'since-code=' . urlencode($value->sinceCode());
            }
            if ($value && strpos('code-desc', $params['order'] ?? '') !== false && $value->sinceCode()) {
                $link['since-code'] = 'since-code=' . urlencode($value->sinceCode());
            }
            return implode('&', $link);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
