<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\RelyingParty\Infrastructure\Driver\Rest;

use Psr\Http\Message\ResponseInterface;
use Psr\Http\Message\ServerRequestInterface;
use OpenApi\Attributes as OA;
use Throwable;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;
use Civi\Lughauth\Features\Access\RelyingParty\Application\Usecase\Retrieve\RelyingPartyRetrieveUsecase;
use Civi\Lughauth\Features\Access\RelyingParty\Application\Usecase\Retrieve\RelyingPartyRetrieveResult;

class RelyingPartyRetrieveController
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly RelyingPartyRetrieveUsecase $retrieveUsecase,
    ) {
    }
    #[OA\Get(
        path: "/api/access/relying-parties{uid}",
        tags: ["Relying party"],
        description:"Create",
        parameters: [
               new OA\PathParameter(name: "uid", required: true, schema: new OA\Schema(type: "string")),
          ],
        responses: [
              new OA\Response(response: 201, description: "Ok", content: new OA\JsonContent(type: "object", ref:"#/components/schemas/RelyingPartyApiDTO")),
              new OA\Response(response: 404, description: "Not found"),
          ]
    )]
    public function retrieve(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("Retrieve Relying party");
        $span = $this->startSpan("Retrieve Relying party");
        try {
            if (!isset($args['uid'])) {
                throw new NotFoundException('-');
            }
            $uid = $args['uid'];
            $result = $this->retrieveUsecase->retrieve($uid);
            $value = $this->mapRelyingParty($result);
            $response->getBody()->write(json_encode($value));
            return $response->withStatus(200)
                  ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    private function mapRelyingParty(RelyingPartyRetrieveResult $value): RelyingPartyApiDTO
    {
        $this->logDebug("Map entity to output dto for Relying party");
        $span = $this->startSpan("Map entity to output dto for Relying party");
        try {
            $dto = new RelyingPartyApiDTO();
            $dto->uid = $value->getUid();
            $dto->code = $value->getCode();
            $dto->apiKey = $value->getApiKey();
            $dto->enabled = $value->getEnabled();
            $dto->scopes = $value->getScopes();
            $dto->schemas = $value->getSchemas();
            $dto->policies = $value->getPolicies();
            $dto->version = $value->getVersion();
            return $dto;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
