<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Role\Application\Policy\Fields;

use Throwable;
use Civi\Lughauth\Shared\Context;
use Civi\Lughauth\Features\Access\Role\Domain\RoleRef;
use Civi\Lughauth\Features\Access\Role\Application\Service\Visibility\RoleFixedFieldsProposal;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class RoleExcludingdRoot
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(private readonly Context $context)
    {
    }
    public function __invoke(RoleFixedFieldsProposal $event): RoleFixedFieldsProposal
    {
        $this->logDebug("Check RoleExcludingdRoot Role");
        $span = $this->startSpan("Check RoleExcludingdRoot Role");
        try {
            $userContext = $this->context->getIdentity();
            if (!$userContext->hasAnyRole('ROOT')) {
                $event->fields = [...$event->fields, ['tenant']];
            }
            return $event;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
