<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Role\Application\Policy\Filter;

use Throwable;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Shared\Context;
use Civi\Lughauth\Features\Access\Role\Application\Service\Visibility\RoleFilterProposal;
use Civi\Lughauth\Features\Access\Role\Domain\Gateway\RoleFilter;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class TenantAccesible
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(private readonly Context $context)
    {
    }
    public function __invoke(RoleFilterProposal $event): RoleFilterProposal
    {
        $this->logDebug("Check TenantAccesible Role");
        $span = $this->startSpan("Check TenantAccesible Role");
        try {
            $userContext = $this->context->getIdentity();
            if (!$userContext->hasAnyRole('ROOT')) {
                if (!$userContext->anonimous && $tenantTenantAccesible = $userContext->tenant) {
                    $event->roleFilter = $event->roleFilter->withTenantTenantAccesible($tenantTenantAccesible);
                } else {
                    throw new UnauthorizedException('The claim tenant is required');
                }
            }
            return $event;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
