<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Role\Application\Policy\Formula;

use Throwable;
use Civi\Lughauth\Features\Access\Tenant\Domain\TenantRef;
use Civi\Lughauth\Features\Access\Role\Domain\ValueObject\RoleTenantVO;
use Civi\Lughauth\Features\Access\Role\Domain\RoleAttributes;
use Civi\Lughauth\Shared\Context;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class TenantCalculator
{
    use LoggerAwareTrait;
    use TracerAwareTrait;
    public function __construct(private readonly Context $context)
    {
    }
    public function calculateTenant(?RoleAttributes $original = null): ?RoleTenantVO
    {
        $this->logDebug("Check TenantCalculator Role");
        $span = $this->startSpan("Check TenantCalculator Role");
        try {
            $userContext = $this->context->getIdentity();
            if (!$userContext->hasAnyRole('root:admin')) {
                $value = $userContext->tenant;
                return RoleTenantVO::from(new TenantRef($value));
            }
            return null;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
