<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Role\Application\Service\Visibility;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Features\Access\Tenant\Application\Service\Visibility\TenantVisibilityService;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Features\Access\Role\Domain\RoleRef;
use Civi\Lughauth\Features\Access\Role\Domain\RoleAttributes;
use Civi\Lughauth\Features\Access\Role\Domain\Role;
use Civi\Lughauth\Features\Access\Role\Domain\Gateway\RoleFilter;
use Civi\Lughauth\Features\Access\Role\Domain\Gateway\RoleCursor;
use Civi\Lughauth\Features\Access\Role\Domain\Gateway\RoleReadGateway;
use Civi\Lughauth\Features\Access\Role\Domain\Gateway\RoleSlide;
use Civi\Lughauth\Features\Access\Role\Domain\Gateway\RoleWriteGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class RoleVisibilityService
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly RoleReadGateway $readGateway,
        private readonly RoleWriteGateway $writeGateway,
        private readonly TenantVisibilityService $tenantVisibilityService
    ) {
    }

    public function copyWithFixed(RoleAttributes $attributes, ?Role $original = null): RoleAttributes
    {
        $this->logDebug("Copy with fixed for Role");
        $span = $this->startSpan("Copy with fixed for  Role");
        try {
            $visible = $this->visiblesReferences($attributes);
            $fixed = $this->fieldsToFix($original);
            foreach ($fixed as $field) {
                $visible->unset($field);
            }
            $result = $this->dispacher->dispatch(new RolePresetProposal($attributes, $original));
            return $result->attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    public function copyWithHidden(RoleAttributes $attributes, ?Role $original = null): RoleAttributes
    {
        $this->logDebug("Copy with hidden for Role");
        $span = $this->startSpan("Copy with hidden for  Role");
        try {
            $hidden = $this->fieldsToHide($original);
            foreach ($hidden as $field) {
                $attributes->unset($field);
            }
            return $attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    public function checkVisibility(\Iterator|string|RoleRef $value): bool
    {
        $this->logDebug("Check visibility of an iterator for Role");
        $span = $this->startSpan("Check visibility of an iterator for  Role");
        try {
            if (is_a($value, \Iterator::class)) {
                $ids = [];
                foreach ($value as $val) {
                    $ids[] = $val->uid();
                }
                $filter = new RoleFilter(uids: $ids);
                return count($ids) == $this->countVisibles($filter);
            } elseif (is_a($value, RoleRef::class)) {
                return !!$this->retrieveVisibleForUpdate($value);
            } else {
                return !!$this->retrieveVisibleForUpdate(new RoleRef($value));
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function listVisibles(RoleFilter $filter, ?RoleCursor $cursor = null): RoleSlide
    {
        $this->logDebug("List visibles for Role");
        $span = $this->startSpan("List visibles for  Role");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            $result = $this->readGateway->list($visibleFilter, $cursor);
            return $result->slide(function ($item) {
                return $this->evaluatePostVisibility($item);
            });
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function countVisibles(RoleFilter $filter): int
    {
        $this->logDebug("Count visibles for Role");
        $span = $this->startSpan("Count visibles for  Role");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            return $this->readGateway->count($visibleFilter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function listVisiblesForUpdate(RoleFilter $filter, ?RoleCursor $cursor = null): RoleSlide
    {
        $this->logDebug("List visibles for update for Role");
        $span = $this->startSpan("List visibles for update for  Role");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            $result = $this->writeGateway->listForUpdate($visibleFilter, $cursor);
            return $result->slide(function ($item) {
                return $this->evaluatePostVisibility($item);
            });
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function countVisiblesForUpdate(RoleFilter $filter): int
    {
        $this->logDebug("Count visibles for update for Role");
        $span = $this->startSpan("Count visibles for update for  Role");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            return $this->writeGateway->countForUpdate($visibleFilter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function retrieveVisible(RoleRef $ref): ?Role
    {
        $this->logDebug("Retrieve visible for Role");
        $span = $this->startSpan("Retrieve visible for  Role");
        try {
            $filter = new RoleFilter(uids: [$ref->uid()]);
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            if ($readed = $this->readGateway->retrieve($visibleFilter)) {
                return $this->evaluatePostVisibility($readed) ? $readed : null;
            } else {
                return null;
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function retrieveVisibleForUpdate(RoleRef $ref): ?Role
    {
        $this->logDebug("Retrieve visible for update for Role");
        $span = $this->startSpan("Retrieve visible for update for  Role");
        try {
            $filter = new RoleFilter(uids: [$ref->uid()]);
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            if ($readed = $this->writeGateway->retrieveForUpdate($visibleFilter)) {
                return $this->evaluatePostVisibility($readed) ? $readed : null;
            } else {
                return null;
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function fieldsToFix(?RoleRef $ref = null): array
    {
        $this->logDebug("Check fields to fix for Role");
        $span = $this->startSpan("Check fields for fix for Role");
        try {
            $result = $this->dispacher->dispatch(new RoleFixedFieldsProposal(Role::calculatedFields(), $ref));
            return array_merge($this->fieldsToHide($ref), $result->fields);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    public function fieldsToHide(?RoleRef $ref = null): array
    {
        $this->logDebug("Check fields to hide for Role");
        $span = $this->startSpan("Check fields to hide for  Role");
        try {
            $result = $this->dispacher->dispatch(new RoleHideFieldsProposal([], $ref));
            return $result->fields;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    private function visiblesReferences(RoleAttributes $attributes): RoleAttributes
    {
        $this->logDebug("Check visibility of parent references for Role");
        $span = $this->startSpan("Check visibility of parent references for  Role");
        try {
            if ($attributes->getTenant() && !$this->tenantVisibilityService->checkVisibility($attributes->getTenant())) {
                throw new NotFoundException("Unknow Tenant " . $attributes->getTenant());
            }
            return $attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function applyPreVisibilityFilter(RoleFilter $filter)
    {
        $this->logDebug("Compose visibility filter for Role");
        $span = $this->startSpan("Compose visibility filter for  Role");
        try {
            $result = $this->dispacher->dispatch(new RoleFilterProposal($filter));
            return $result->roleFilter;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function evaluatePostVisibility(Role $value): bool
    {
        $this->logDebug("Check if item is visible for Role");
        $span = $this->startSpan("Check if item is visible for  Role");
        try {
            $result = $this->dispacher->dispatch(new RoleVisibilityProposal(true, $value));
            return $result->visible;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
