<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Role\Application\Usecase\Create;

use Civi\Lughauth\Features\Access\Role\Domain\ValueObject\Holder\RoleUidAttributeHolder;
use Civi\Lughauth\Features\Access\Role\Domain\ValueObject\Holder\RoleNameAttributeHolder;
use Civi\Lughauth\Features\Access\Role\Domain\ValueObject\Holder\RoleRelyingPartyAttributeHolder;
use Civi\Lughauth\Features\Access\Role\Domain\ValueObject\Holder\RoleVersionAttributeHolder;
use Civi\Lughauth\Features\Access\Role\Domain\RoleAttributes;

class RoleCreateParams
{
    use RoleUidAttributeHolder;
    use RoleNameAttributeHolder;
    use RoleRelyingPartyAttributeHolder;
    use RoleVersionAttributeHolder;

    private const UNSETS = [
      'uid' => 'unsetUid',
      'name' => 'unsetName',
      'relyingParty' => 'unsetRelyingParty',
      'version' => 'unsetVersion',
    ];

    public function __construct(RoleAttributes|null $att = null)
    {
        if ($att) {
            $this->uid($att->getUidOrDefault(null));
            $this->name($att->getNameOrDefault(null));
            $this->relyingParty($att->getRelyingPartyOrDefault(null));
            $this->version($att->getVersionOrDefault(null));
        }
    }
    public function toAttributes(): RoleAttributes
    {
        $att = new RoleAttributes();
        if ($this->uid) {
            $att->uid($this->uid);
        }
        if ($this->name) {
            $att->name($this->name);
        }
        if ($this->relyingParty) {
            $att->relyingParty($this->relyingParty);
        }
        if ($this->version) {
            $att->version($this->version);
        }
        return $att;
    }
    public function unset($field)
    {
        if (isset(self::UNSETS[$field])) {
            call_user_func([$this, self::UNSETS[$field]]);
        }
    }
    public function withDefaults(): RoleCreateParams
    {
        $this->withDefaultUid();
        $this->withDefaultName();
        $this->withDefaultRelyingParty();
        $this->withDefaultVersion();
        return $this;
    }
}
