<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Role\Application\Usecase\Create;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Features\Access\Role\Application\Service\Visibility\RoleVisibilityService;
use Civi\Lughauth\Features\Access\Role\Domain\Role;
use Civi\Lughauth\Features\Access\Role\Domain\Gateway\RoleWriteGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;
use Civi\Lughauth\Features\Access\Role\Application\Policy\Formula\TenantCalculator;

class RoleCreateUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly TenantCalculator $tenantFormula,
        private readonly EventDispatcherInterface $dispacher,
        private readonly RoleVisibilityService $visibility,
        private readonly RoleWriteGateway $writer
    ) {
    }

    public function allowCreate(): Allow
    {
        $this->logDebug("Check allow of create usecase for Role");
        $span = $this->startSpan("Check allow of create usecase for Role");
        try {
            $result = $this->dispacher->dispatch(new RoleCreateAllowDecision(Allow::allowed('create', 'Allowed to create by default')));
            return $result->getAllow();
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function create(RoleCreateParams $params): RoleCreateResult
    {
        $this->logDebug("Run create usecase for Role");
        $span = $this->startSpan("Run create usecase for Role");
        try {
            $allow = $this->allowCreate();
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            $this->dispacher->dispatch(new RoleCreateCheck($params));
            $enriched = $this->dispacher->dispatch(new RoleCreateEnrich($params, $params->toAttributes()));
            $attributes = $enriched->getResult();
            $input = $this->visibility->copyWithFixed($attributes);
            $input->tenant($this->tenantFormula->calculateTenant($input));
            $entity = Role::create($input);
            $result = $this->writer->create(
                $entity,
                fn ($created) =>
                            $this->visibility->checkVisibility($created)
            );
            $output = $this->visibility->copyWithHidden($this->visibility->prepareVisibleData($result));
            return new RoleCreateResult($output);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
