<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Role\Application\Usecase\Update;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Features\Access\Role\Domain\RoleRef;
use Civi\Lughauth\Features\Access\Role\Application\Service\Visibility\RoleVisibilityService;
use Civi\Lughauth\Features\Access\Role\Domain\Gateway\RoleWriteGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class RoleUpdateUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly RoleVisibilityService $visibility,
        private readonly RoleWriteGateway $writer
    ) {
    }

    public function allowUpdate(?RoleRef $ref = null): Allow
    {
        $this->logDebug("Check allow update usecase for Role");
        $span = $this->startSpan("Check allow update usecase for Role");
        try {
            $result = $this->dispacher->dispatch(new RoleUpdateAllowDecision(Allow::allowed('update', 'Allowed to update by default'), $ref));
            return $result->getAllow();
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function fixedFields(?RoleRef $ref = null): array
    {
        $this->logDebug("Check fixed fields for update usecase for Role");
        $span = $this->startSpan("Check fixed fields for update usecase for Role");
        try {
            return $this->visibility->fieldsToFix($ref);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function update(string $uid, RoleUpdateParams $params): RoleUpdateResult
    {
        $this->logDebug("Run update usecase for Role");
        $span = $this->startSpan("Run update usecase for Role");
        try {
            $ref = new RoleRef($uid);
            $allow = $this->allowUpdate($ref);
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            if (!$original = $this->visibility->retrieveVisibleForUpdate($ref)) {
                throw new NotFoundException($uid);
            }
            $this->dispacher->dispatch(new RoleUpdateCheck($params, $original));
            $enriched = $this->dispacher->dispatch(new RoleUpdateEnrich($params, $original, $params->toAttributes()));
            $attributes = $enriched->getResult();
            $input = $this->visibility->copyWithFixed($attributes);
            $modified = $original->replace($input);
            $result = $this->writer->update($original, $modified);
            $output = $this->visibility->copyWithHidden($this->visibility->prepareVisibleData($result));
            return new RoleUpdateResult($output);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
