<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Role\Domain\Gateway;

use Psr\Container\ContainerInterface;
use Civi\Lughauth\Features\Access\Tenant\Domain\TenantRef;
use Civi\Lughauth\Features\Access\Role\Domain\Role;
use Civi\Lughauth\Features\Access\Role\Infrastructure\Driven\RoleReadRepositoryAdapter;
use Civi\Lughauth\Features\Access\Role\Domain\RoleRef;

class RoleReadGateway
{
    private RoleReadRepository $repository;
    public function __construct(ContainerInterface $container, ?RoleReadRepository $repository = null)
    {
        $this->repository = $repository ?? $container->get(RoleReadRepositoryAdapter::class);
    }
    public function enrich(RoleRef $ref): ?Role
    {
        return $this->repository->enrich($ref);
    }
    public function list(?RoleFilter $filter = null, ?RoleCursor $cursor = null): RoleSlide
    {
        return $this->repository->list($filter, $cursor);
    }
    public function retrieve(RoleFilter $filter): ?Role
    {
        return $this->repository->retrieve($filter);
    }
    public function exists(?RoleFilter $filter): bool
    {
        return $this->repository->exists($filter);
    }
    public function count(?RoleFilter $filter = null): int
    {
        return $this->repository->count($filter);
    }
    public function findOneByUid(string $uid): ?Role
    {
        return $this->repository->findOneByUid($uid);
    }
    public function findOneByTenantAndName(?TenantRef $tenant, string $name): ?Role
    {
        return $this->repository->findOneByTenantAndName($tenant, $name);
    }
}
