<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Role\Domain\Gateway;

use Civi\Lughauth\Features\Access\Role\Domain\Role;

class RoleSlide extends \ArrayIterator
{
    private readonly ?Role $last;
    private readonly RoleCursor $nextCursor;
    public function __construct(
        private readonly ?\Closure $next,
        private readonly ?RoleCursor $cursor,
        private readonly array $values
    ) {
        parent::__construct($values);
        $this->last = $values ? end($values) : null;
        $this->nextCursor = new RoleCursor(
            limit: $this->cursor->limit(),
            sinceUid: $this->last ? $this->last->uid() : null,
            sinceName: $this->last ? $this->last->getName() : null,
        );
    }
    public function cursor(): ?RoleCursor
    {
        return $this->cursor;
    }
    public function nextCursor(): RoleCursor
    {
        return $this->nextCursor;
    }
    public function values(): array
    {
        return $this->values;
    }
    public function current(): Role
    {
        return parent::current();
    }
    public function map(\Closure $fn): RoleSlide
    {
        return new RoleSlide($this->next, $this->cursor, array_map($fn, $this->values));
    }
    public function slide(\Closure $fn): RoleSlide
    {
        $filtered = array_filter($this->values, $fn);
        $collected = $filtered;
        $last = $this->last;
        $next = $this->next;
        $cursor = $this->cursor;
        if (count($collected) == $cursor->limit()) {
            while (
                count($collected) < count($this->values) && $next
            ) {
                $nextSlide = ($this->next)($this, $cursor->next($last, $cursor->limit()));
                if (count($nextSlide)) {
                    $nextFiltered = array_filter($nextSlide->values, $fn);
                    $collected = array_merge($collected, $nextFiltered);
                    $cursor = $nextSlide->cursor;
                    $next = $nextSlide->next;
                    $last = $nextSlide->last;
                } else {
                    break;
                }
            }
            return new RoleSlide($next, $cursor, $collected);
        } else {
            return $this;
        }
    }
}
