<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Role\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\Role\Domain\ValueObject\RoleDomainsVO;
use Civi\Lughauth\Features\Access\Role\Domain\ValueObject\RoleDomainsListRef;

trait RoleDomainsAccesor
{
    private RoleDomainsVO $_domains;
    public function getDomains(): ?RoleDomainsListRef
    {
        return $this->_domains->value();
    }
    public function domains(): RoleDomainsVO
    {
        return $this->_domains;
    }
    public function isDomainsChanged(?self $original = null): bool
    {
        return $this->_domains->value() !== ($original ? $original->_domains->value() : null);
    }
    public function withDomains(RoleDomainsVO|RoleDomainsListRef| array |null $domains): self
    {
        $copy = clone $this;
        $copy->_domains = RoleDomainsVO::from($domains);
        return $copy;
    }
}
