<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Role\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\Role\Domain\ValueObject\RoleNameVO;

trait RoleNameAccesor
{
    private RoleNameVO $_name;
    public function getName(): string
    {
        return $this->_name->value();
    }
    public function name(): RoleNameVO
    {
        return $this->_name;
    }
    public function isNameChanged(?self $original = null): bool
    {
        return $this->_name->value() !== ($original ? $original->_name->value() : null);
    }
    public function withName(RoleNameVO|string $name): self
    {
        $copy = clone $this;
        $copy->_name = RoleNameVO::from($name);
        return $copy;
    }
}
