<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Role\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\Role\Domain\ValueObject\RoleRelyingPartyVO;
use Civi\Lughauth\Features\Access\RelyingParty\Domain\RelyingPartyRef;

trait RoleRelyingPartyAccesor
{
    private ?RoleRelyingPartyVO $_relyingParty = null;
    public function getRelyingParty(): ?RelyingPartyRef
    {
        return $this->_relyingParty?->value();
    }
    public function relyingParty(): ?RoleRelyingPartyVO
    {
        return $this->_relyingParty;
    }
    public function isRelyingPartyChanged(?self $original = null): bool
    {
        return $this->_relyingParty->value() !== ($original ? $original->_relyingParty->value() : null);
    }
    public function withRelyingParty(RoleRelyingPartyVO|RelyingPartyRef|null $relyingParty): self
    {
        $copy = clone $this;
        $copy->_relyingParty = RoleRelyingPartyVO::from($relyingParty);
        return $copy;
    }
}
