<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Role\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\Role\Domain\ValueObject\RoleTenantVO;
use Civi\Lughauth\Features\Access\Tenant\Domain\TenantRef;

trait RoleTenantAccesor
{
    private ?RoleTenantVO $_tenant = null;
    public function getTenant(): ?TenantRef
    {
        return $this->_tenant?->value();
    }
    public function tenant(): ?RoleTenantVO
    {
        return $this->_tenant;
    }
    public function isTenantChanged(?self $original = null): bool
    {
        return $this->_tenant->value() !== ($original ? $original->_tenant->value() : null);
    }
    public function withTenant(RoleTenantVO|TenantRef|null $tenant): self
    {
        $copy = clone $this;
        $copy->_tenant = RoleTenantVO::from($tenant);
        return $copy;
    }
}
