<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Role\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\Role\Domain\ValueObject\RoleVersionVO;

trait RoleVersionAccesor
{
    private ?RoleVersionVO $_version = null;
    public function getVersion(): ?int
    {
        return $this->_version?->value();
    }
    public function version(): ?RoleVersionVO
    {
        return $this->_version;
    }
    public function isVersionChanged(?self $original = null): bool
    {
        return $this->_version->value() !== ($original ? $original->_version->value() : null);
    }
    public function withVersion(RoleVersionVO|int|null $version): self
    {
        $copy = clone $this;
        $copy->_version = RoleVersionVO::from($version);
        return $copy;
    }
}
