<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Role\Domain\ValueObject\Holder;

use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Features\Access\Role\Domain\ValueObject\RoleNameVO;

trait RoleNameAttributeHolder
{
    protected RoleNameVO|string|null $name = null;
    protected bool $nameAssigned = false;

    public function getNameOrDefault(?RoleNameVO $name): RoleNameVO|string|null
    {
        return $this->nameAssigned ? ($this->name !== null ? RoleNameVO::from($this->name) : null) : $name;
    }
    public function name(RoleNameVO|string|null $name): static
    {
        $this->name = $name;
        $this->nameAssigned = true;
        return $this;
    }
    public function getName(): ?string
    {
        return is_a($this->name, RoleNameVO::class) ? $this->name->value() : $this->name;
    }
    public function unsetName(): static
    {
        $this->name = null;
        $this->nameAssigned = false;
        return $this;
    }
    protected function withDefaultName(): void
    {
    }
    protected function withAssertedNameRules(self $value, ConstraintFailList $errorsList): void
    {
        if ($value->nameAssigned = $this->nameAssigned) {
            $value->name = RoleNameVO::tryFrom($this->name, $errorsList);
        }
    }
}
