<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Role\Domain\ValueObject;

use Civi\Lughauth\Features\Access\Role\Domain\ValueObject\Accesor\RoleDomainsSecurityDomainAccesor;
use Civi\Lughauth\Features\Access\Role\Domain\ValueObject\Accesor\RoleDomainsVersionAccesor;

class RoleDomainsItem
{
    use RoleDomainsSecurityDomainAccesor;
    use RoleDomainsVersionAccesor;
    private readonly RoleDomainsUidVO $_uid;
    public function __construct(
        RoleDomainsUidVO|null $uid,
        RoleDomainsSecurityDomainVO|null $securityDomain,
        RoleDomainsVersionVO|null $version,
    ) {
        $this->_uid = $uid;
        $this->_securityDomain = $securityDomain;
        $this->_version = $version;
    }
    public function uid(): string
    {
        return $this->_uid->value();
    }
    public function getOrDefault(RoleDomainsItem $base): RoleDomainsItem
    {
        return new RoleDomainsItem(
            $this->_uid ??  $base->_uid,
            $this->_securityDomain ??  $base->_securityDomain,
            $this->_version ??  $base->_version,
        );
    }
}
