<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Role\Domain\ValueObject;

use InvalidArgumentException;

class RoleDomainsListRef implements \Iterator
{
    public static function fromArray(array $items): self
    {
        foreach ($items as $item) {
            if (!$item instanceof RoleDomainsItem) {
                throw new InvalidArgumentException("All items must be of type RoleDomainsItem");
            }
        }
        return new self(...$items);
    }

    private array $domains = [];

    public function __construct(RoleDomainsItem ...$domains)
    {
        $this->domains = $domains;
    }

    public function current(): RoleDomainsItem
    {
        return current($this->domains);
    }
    public function key(): int
    {
        return key($this->domains);
    }
    public function next(): void
    {
        next($this->domains);
    }
    public function rewind(): void
    {
        reset($this->domains);
    }
    public function valid(): bool
    {
        return key($this->domains) !== null;
    }
}
