<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Role\Domain\ValueObject;

use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFail;

class RoleDomainsVO
{
    public static function from(RoleDomainsVO|RoleDomainsListRef| array |null $value): RoleDomainsVO
    {
        if (is_a($value, RoleDomainsVO::class)) {
            // If is a ValueObject, its already validated
            return $value;
        } else {
            // If is not a ValueObject, validation is need and exception throw
            $errorsList = new ConstraintFailList();
            return self::tryFrom($value, $errorsList);
        }
    }
    public static function tryFrom(mixed $value, ConstraintFailList $list): ?RoleDomainsVO
    {
        if (is_a($value, RoleDomainsVO::class)) {
            // If is a ValueObject, its already validated... nothing to append
            return $value;
        } elseif (is_array($value)) {
            return new RoleDomainsVO(RoleDomainsListRef::fromArray($value));
        } elseif (!$value) {
            return new RoleDomainsVO($value);
        } elseif (is_a($value, RoleDomainsListRef::class)) {
            return new RoleDomainsVO($value);
        } else {
            $list->add(new ConstraintFail('wrong_type', ['domains'], [$value], ['RoleDomainsListRef']));
            return null;
        }
    }
    /**
     * private constructor to avoid build a value without all the rule validations.
     */
    private function __construct(
        private readonly ?RoleDomainsListRef $domains
    ) {
    }
    public function value(): ?RoleDomainsListRef
    {
        $value = $this->domains;
        if ($value) {
            $value->rewind();
        }
        return $value;
    }
    public function find(string $uid): ?RoleDomainsItem
    {
        foreach ($this->domains as $item) {
            if ($item->uid() == $uid) {
                return $item;
            }
        }
        return null;
    }
    public function getWithDefaults(RoleDomainsVO $defs): ?RoleDomainsVO
    {
        $values = [];
        $originals = $defs->value();
        foreach ($this->domains as $item) {
            $prev = null;
            foreach ($originals as $original) {
                if ($item->uid() == $original->uid()) {
                    $prev = $original;
                    break;
                }
            }
            $values[] = $prev ? $item->getOrDefault($prev) : $item;
        }
        return RoleDomainsVO::from(RoleDomainsListRef::fromArray($values));
    }
}
