<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Role\Domain\ValueObject;

use Civi\Lughauth\Features\Access\RelyingParty\Domain\RelyingPartyRef;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFail;

class RoleRelyingPartyVO
{
    public static function empty(): RoleRelyingPartyVO
    {
        return new RoleRelyingPartyVO(null);
    }
    public static function from(RoleRelyingPartyVO|RelyingPartyRef|null $value): RoleRelyingPartyVO
    {
        if (is_a($value, RoleRelyingPartyVO::class)) {
            // If is a ValueObject, its already validated
            return $value;
        } else {
            // If is not a ValueObject, validation is need and exception throw
            $errorsList = new ConstraintFailList();
            $candidate = self::tryFrom($value, $errorsList);
            if ($errorsList->hasErrors()) {
                throw $errorsList->asConstraintException();
            }
            return $candidate;
        }
    }
    public static function tryFrom(mixed $value, ConstraintFailList $list): ?RoleRelyingPartyVO
    {
        if (is_a($value, RoleRelyingPartyVO::class)) {
            // If is a ValueObject, its already validated... nothing to append
            return $value;
        } elseif (!$value) {
            return new RoleRelyingPartyVO($value);
        } elseif (is_a($value, RelyingPartyRef::class)) {
            return new RoleRelyingPartyVO($value);
        } else {
            $list->add(new ConstraintFail('wrong_type', ['relyingParty'], [$value], ['RelyingPartyRef']));
            return null;
        }
    }
    /**
     * private constructor to avoid build a value without all the rule validations.
     */
    private function __construct(
        private readonly ?RelyingPartyRef $relyingParty
    ) {
    }
    public function value(): ?RelyingPartyRef
    {
        return $this->relyingParty;
    }
}
