    private function checkDuplicates(Role $entity, bool $creation)
    {
        $this->logDebug("Query to check duplicates for Role");
        $span = $this->startSpan("Query to check duplicates for Role");
        try {
            $values = ['uid' => $entity->uid()];
            if ($creation &&  $this->db->exists('SELECT  "uid" from "access_role" where "uid" = :uid', $values)) {
                throw ConstraintException::ofError('not-unique', array_keys($values), array_values($values));
            }
            $values = ['tenant' => $entity->getTenant()?->uid(), 'name' => $entity->getName(), 'uid' => $entity->uid()];
            if ($this->db->exists('SELECT  "tenant", "name" from "access_role" where "tenant" = :tenant and "name" = :name and "uid" != :uid', $values)) {
                throw ConstraintException::ofError('not-unique', ['tenant', 'name'], [$entity->getTenant()?->uid(), $entity->getName()]);
            }
