<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Role\Infrastructure\Driven;

use Override;
use Throwable;
use Civi\Lughauth\Features\Access\Tenant\Domain\TenantRef;
use Civi\Lughauth\Features\Access\Role\Infrastructure\Connector\Pdo\RolePdoConnector;
use Civi\Lughauth\Features\Access\Role\Domain\Gateway\RoleReadRepository;
use Civi\Lughauth\Features\Access\Role\Domain\Gateway\RoleSlide;
use Civi\Lughauth\Features\Access\Role\Domain\Gateway\RoleFilter;
use Civi\Lughauth\Features\Access\Role\Domain\Gateway\RoleCursor;
use Civi\Lughauth\Features\Access\Role\Domain\RoleRef;
use Civi\Lughauth\Features\Access\Role\Domain\Role;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class RoleReadRepositoryAdapter implements RoleReadRepository
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly RolePdoConnector $conn,
    ) {
    }
    #[Override]
    public function resolve(RoleRef $ref): ?Role
    {
        if ($ref instanceof Role) {
            return $ref;
        } elseif (!$ref->_private_resolve) {
            $ref->_private_resolve = $this->conn->retrieve(new RoleFilter(uids: [ $ref->uid() ]));
        }
        return $ref->_private_resolve;
    }
    #[Override]
    public function list(?RoleFilter $filter = null, ?RoleCursor $sort = null): RoleSlide
    {
        $this->logDebug("List for Role on adapter ");
        $span = $this->startSpan("List for Role on adapter");
        try {
            $values = $this->conn->list($filter, $sort);
            $last = end($values);
            return new RoleSlide(function ($slide, $next) use ($filter) {
                return $this->list($filter, $next);
            }, new RoleCursor($sort?->limit ?? 100, $last->uid ?? null), $values);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function retrieve(?RoleFilter $filter): ?Role
    {
        $this->logDebug("Retrieve for Role on adapter ");
        $span = $this->startSpan("Retrieve for Role on adapter");
        try {
            return $this->conn->retrieve($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function exists(?RoleFilter $filter): bool
    {
        $this->logDebug("Exists for Role on adapter ");
        $span = $this->startSpan("Exists for Role on adapter");
        try {
            return $this->conn->exists($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function count(?RoleFilter $filter = null): int
    {
        $this->logDebug("Count for Role on adapter ");
        $span = $this->startSpan("Count for Role on adapter");
        try {
            return $this->conn->count($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function findOneByUid(string $uid): ?Role
    {
        $this->logDebug("Find on by uid for Role on adapter");
        $span = $this->startSpan("Find on by uid for Role on adapter");
        try {
            return $this->conn->retrieve(new RoleFilter(uids: [$uid]));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function findOneByTenantAndName(?TenantRef $tenant, string $name): ?Role
    {
        $this->logDebug("Find on by tenant name for Role on adapter");
        $span = $this->startSpan("Find on by tenant name for Role on adapter");
        try {
            return $this->conn->retrieve(new RoleFilter(tenantAndName: RoleFilter::tenantAndNameFilter(tenant: $tenant, name: $name)));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
