<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Role\Infrastructure\Driver\Rest;

use Psr\Http\Message\ResponseInterface;
use Psr\Http\Message\ServerRequestInterface;
use Throwable;
use OpenApi\Attributes as OA;
use Civi\Lughauth\Features\Access\Role\Domain\Gateway\RoleFilter;
use Civi\Lughauth\Features\Access\Role\Domain\Gateway\RoleCursor;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;
use Civi\Lughauth\Features\Access\Role\Application\Usecase\List\RoleListUsecase;
use Civi\Lughauth\Features\Access\Tenant\Domain\TenantRef;

class RoleListController
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly RoleListUsecase $listUsecase,
        private readonly RoleRestMapper $mapper,
    ) {
    }
    #[OA\Get(
        path: "/api/access/roles",
        tags: ["Role"],
        description:"Create",
        responses: [
        new OA\Response(response: 201, description: "Ok", content: new OA\JsonContent(type: "object", ref:"#/components/schemas/RoleApiListDTO")),
        new OA\Response(response: 422, description: "Constraint error"),
    ]
    )
    ]
    public function list(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("List Role");
        $span = $this->startSpan("List Role");
        try {
            $params = $request->getQueryParams();
            $filter = new RoleFilter(
                uids: isset($params['uid']) ? [$params['uid']] : (isset($params['uids']) ? explode(',', $params['uids']) : null),
                search: $params['search'] ?? null,
                name: $params['name'] ?? null,
                tenant: isset($params['tenant']) ? new TenantRef($params['tenant']) : null,
                tenants: isset($params['tenants']) ? explode(",", $params['tenants']) : null,
            );
            $cursor = new RoleCursor(
                limit: (int)($params['limit'] ?? 100),
                sinceUid: $params['since-uid'] ?? null,
                sinceName: $params['since-name'] ?? null,
                order: isset($params['order']) ? $this->extractOrder(explode(',', $params['order'])) : null,
            );
            $result = $this->listUsecase->list($filter, $cursor);
            $value = ['items' => array_map(fn ($item) => $this->mapper->mapRole($item), $result->values())];
            if ($nextLink = $this->nextLink($result->cursor(), $params)) {
                $value['next'] = "?{$nextLink}";
            }
            $response->getBody()->write(json_encode($value));
            return $response->withStatus(200)
              ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function extractOrder(array $order): array
    {
        $this->logDebug("Extract order for Role");
        $span = $this->startSpan("Extract order for Role");
        try {
            $mapped = [];
            foreach ($order as $ord) {
                if ($ord === 'name-asc') {
                    $mapped[] = 'nameAsc';
                }
                if ($ord === 'name-desc') {
                    $mapped[] = 'nameDesc';
                }
            }
            return $mapped;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function nextLink(?RoleCursor $value, array $params): ?string
    {
        $this->logDebug("Check generic allow for Role");
        $span = $this->startSpan("Generic allow for Role");
        try {
            if (!$value) {
                return null;
            }
            $link = [];
            if (isset($params['limit'])) {
                $link['limit'] = 'limit=' . $params['limit'];
            }
            if ($value && $value->sinceUid()) {
                $link['since-uid'] = 'since-uid=' . urlencode($value->sinceUid());
            }
            if ($value && strpos('NAME-ASC', $params['order'] ?? '') !== false && $value->sinceName()) {
                $link['since-name'] = 'since-name=' . urlencode($value->sinceName());
            }
            if ($value && strpos('NAME-DESC', $params['order'] ?? '') !== false && $value->sinceName()) {
                $link['since-name'] = 'since-name=' . urlencode($value->sinceName());
            }
            return implode('&', $link);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
