<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Role\Infrastructure\Driver\Rest;

use Psr\Http\Message\ServerRequestInterface;
use Throwable;
use Civi\Lughauth\Features\Access\Role\Domain\ValueObject\RoleUidVO;
use Civi\Lughauth\Features\Access\Role\Domain\ValueObject\RoleNameVO;
use Civi\Lughauth\Features\Access\Tenant\Domain\TenantRef;
use Civi\Lughauth\Features\Access\Role\Domain\ValueObject\RoleTenantVO;
use Civi\Lughauth\Features\Access\Role\Domain\ValueObject\RoleVersionVO;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Features\Access\Role\Domain\RoleAttributes;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class RoleRestMapper
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function readRole(ServerRequestInterface $request): RoleAttributes
    {
        $this->logDebug("Read entity for Role");
        $span = $this->startSpan("Read entity for Role");
        try {
            $body = $request->getParsedBody();
            $dto = new RoleApiDTO();
            $dto->uid = $body['uid'] ?? null;
            $dto->name = $body['name'] ?? null;
            $dto->tenant = $body['tenant'] ?? null;
            $dto->version = $body['version'] ?? null;
            return $this->mapRoleApiDTO($dto);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function mapRoleApiDTO(RoleApiDTO $body): RoleAttributes
    {
        $this->logDebug("Map input dto to entity for Role");
        $span = $this->startSpan("Map input dto to entity for Role");
        try {
            $errorsList = new ConstraintFailList();
            $value = new RoleAttributes();
            $value->uid(RoleUidVO::tryFrom($body->uid ?? null, $errorsList));
            $value->name(RoleNameVO::tryFrom($body->name ?? null, $errorsList));
            if (isset($body->tenant) && isset($body->tenant['$ref'])) {
                $value->tenant(RoleTenantVO::tryFrom(new TenantRef(uid: $body->tenant['$ref']), $errorsList));
            }
            $value->version(RoleVersionVO::tryFrom($body->version ?? null, $errorsList));
            if ($errorsList->hasErrors()) {
                throw $errorsList->asConstraintException();
            }
            return $value;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function mapRole(RoleAttributes $value): RoleApiDTO
    {
        $this->logDebug("Map entity to output dto for Role");
        $span = $this->startSpan("Map entity to output dto for Role");
        try {
            $tenant = $value->getTenant();
            $dto = new RoleApiDTO();
            $dto->uid = $value->getUid();
            $dto->name = $value->getName();
            $dto->tenant = $tenant ? ['$ref' => $tenant->uid()] : null;
            $dto->version = $value->getVersion();
            return $dto;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
