<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\Role\Infrastructure\Driver\Rest;

use Psr\Http\Message\ResponseInterface;
use Psr\Http\Message\ServerRequestInterface;
use OpenApi\Attributes as OA;
use Throwable;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;
use Civi\Lughauth\Features\Access\Role\Application\Usecase\Retrieve\RoleRetrieveUsecase;
use Civi\Lughauth\Features\Access\Role\Application\Usecase\Retrieve\RoleRetrieveResult;

class RoleRetrieveController
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly RoleRetrieveUsecase $retrieveUsecase,
    ) {
    }
    #[OA\Get(
        path: "/api/access/roles{uid}",
        tags: ["Role"],
        description:"Create",
        parameters: [
               new OA\PathParameter(name: "uid", required: true, schema: new OA\Schema(type: "string")),
          ],
        responses: [
              new OA\Response(response: 201, description: "Ok", content: new OA\JsonContent(type: "object", ref:"#/components/schemas/RoleApiDTO")),
              new OA\Response(response: 404, description: "Not found"),
          ]
    )]
    public function retrieve(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("Retrieve Role");
        $span = $this->startSpan("Retrieve Role");
        try {
            if (!isset($args['uid'])) {
                throw new NotFoundException('-');
            }
            $uid = $args['uid'];
            $result = $this->retrieveUsecase->retrieve($uid);
            $value = $this->mapRole($result);
            $response->getBody()->write(json_encode($value));
            return $response->withStatus(200)
                  ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    private function mapRole(RoleRetrieveResult $value): RoleApiDTO
    {
        $this->logDebug("Map entity to output dto for Role");
        $span = $this->startSpan("Map entity to output dto for Role");
        try {
            $tenant = $value->getTenant();
            $dto = new RoleApiDTO();
            $dto->uid = $value->getUid();
            $dto->name = $value->getName();
            $dto->tenant = $tenant ? ['$ref' => $tenant->uid()] : null;
            $domains = [];
            foreach ($value->getDomains() as $item) {
                $domains[] = [
                  'uid' => $item->uid(),
                  'securityDomain' => $item->getSecurityDomain() ? ['$ref' => $item->getSecurityDomain()->uid() ] : null,
                  'version' => $item->getVersion(),
                 ];
            }
            $dto->domains = $domains;
            $dto->version = $value->getVersion();
            return $dto;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
