<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\ScopeAssignation\Application\Policy\Allow\Update;

use Throwable;
use Civi\Lughauth\Features\Access\ScopeAssignation\Application\Usecase\Update\ScopeAssignationUpdateAllowDecision;
use Civi\Lughauth\Shared\Context;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class IsAutenticatedUpdateAllow
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(private readonly Context $context)
    {
    }
    public function __invoke(ScopeAssignationUpdateAllowDecision $proposal): ScopeAssignationUpdateAllowDecision
    {
        $this->logDebug("Check IsAutenticatedUpdateAllow Scope assignation");
        $span = $this->startSpan("Check IsAutenticatedUpdateAllow Scope assignation");
        try {
            $userContext = $this->context->getIdentity();
            if ($proposal->allow->allowed) {
                $belongs = !$userContext->anonimous;
                $allowed = $belongs ? Allow::allowed('update', 'Enabled for user IsAutenticatedUpdate') : Allow::disallowed('update', 'Disabled if not IsAutenticatedUpdate');
                $proposal->allow = $allowed;
            }
            return $proposal;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
