<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\ScopeAssignation\Application\Service\Visibility;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Features\Access\SecurityDomain\Application\Service\Visibility\SecurityDomainVisibilityService;
use Civi\Lughauth\Features\Access\SecurityScope\Application\Service\Visibility\SecurityScopeVisibilityService;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Features\Access\ScopeAssignation\Domain\ScopeAssignationRef;
use Civi\Lughauth\Features\Access\ScopeAssignation\Domain\ScopeAssignationAttributes;
use Civi\Lughauth\Features\Access\ScopeAssignation\Domain\ScopeAssignation;
use Civi\Lughauth\Features\Access\ScopeAssignation\Domain\Gateway\ScopeAssignationFilter;
use Civi\Lughauth\Features\Access\ScopeAssignation\Domain\Gateway\ScopeAssignationCursor;
use Civi\Lughauth\Features\Access\ScopeAssignation\Domain\Gateway\ScopeAssignationReadGateway;
use Civi\Lughauth\Features\Access\ScopeAssignation\Domain\Gateway\ScopeAssignationSlide;
use Civi\Lughauth\Features\Access\ScopeAssignation\Domain\Gateway\ScopeAssignationWriteGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class ScopeAssignationVisibilityService
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly ScopeAssignationReadGateway $readGateway,
        private readonly ScopeAssignationWriteGateway $writeGateway,
        private readonly SecurityDomainVisibilityService $securityDomainVisibilityService,
        private readonly SecurityScopeVisibilityService $securityScopeVisibilityService
    ) {
    }

    public function copyWithFixed(ScopeAssignationAttributes $attributes, ?ScopeAssignation $original = null): ScopeAssignationAttributes
    {
        $this->logDebug("Copy with fixed for Scope assignation");
        $span = $this->startSpan("Copy with fixed for  Scope assignation");
        try {
            $visible = $this->visiblesReferences($attributes);
            $fixed = $this->fieldsToFix($original);
            foreach ($fixed as $field) {
                $visible->unset($field);
            }
            $result = $this->dispacher->dispatch(new ScopeAssignationPresetProposal($attributes, $original));
            return $result->attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    public function copyWithHidden(ScopeAssignationAttributes $attributes, ?ScopeAssignation $original = null): ScopeAssignationAttributes
    {
        $this->logDebug("Copy with hidden for Scope assignation");
        $span = $this->startSpan("Copy with hidden for  Scope assignation");
        try {
            $hidden = $this->fieldsToHide($original);
            foreach ($hidden as $field) {
                $attributes->unset($field);
            }
            return $attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    public function checkVisibility(\Iterator|string|ScopeAssignationRef $value): bool
    {
        $this->logDebug("Check visibility of an iterator for Scope assignation");
        $span = $this->startSpan("Check visibility of an iterator for  Scope assignation");
        try {
            if (is_a($value, \Iterator::class)) {
                $ids = [];
                foreach ($value as $val) {
                    $ids[] = $val->uid();
                }
                $filter = new ScopeAssignationFilter(uids: $ids);
                return count($ids) == $this->countVisibles($filter);
            } elseif (is_a($value, ScopeAssignationRef::class)) {
                return !!$this->retrieveVisibleForUpdate($value);
            } else {
                return !!$this->retrieveVisibleForUpdate(new ScopeAssignationRef($value));
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function listVisibles(ScopeAssignationFilter $filter, ?ScopeAssignationCursor $cursor = null): ScopeAssignationSlide
    {
        $this->logDebug("List visibles for Scope assignation");
        $span = $this->startSpan("List visibles for  Scope assignation");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            $result = $this->readGateway->list($visibleFilter, $cursor);
            return $result->slide(function ($item) {
                return $this->evaluatePostVisibility($item);
            });
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function countVisibles(ScopeAssignationFilter $filter): int
    {
        $this->logDebug("Count visibles for Scope assignation");
        $span = $this->startSpan("Count visibles for  Scope assignation");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            return $this->readGateway->count($visibleFilter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function listVisiblesForUpdate(ScopeAssignationFilter $filter, ?ScopeAssignationCursor $cursor = null): ScopeAssignationSlide
    {
        $this->logDebug("List visibles for update for Scope assignation");
        $span = $this->startSpan("List visibles for update for  Scope assignation");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            $result = $this->writeGateway->listForUpdate($visibleFilter, $cursor);
            return $result->slide(function ($item) {
                return $this->evaluatePostVisibility($item);
            });
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function countVisiblesForUpdate(ScopeAssignationFilter $filter): int
    {
        $this->logDebug("Count visibles for update for Scope assignation");
        $span = $this->startSpan("Count visibles for update for  Scope assignation");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            return $this->writeGateway->countForUpdate($visibleFilter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function retrieveVisible(ScopeAssignationRef $ref): ?ScopeAssignation
    {
        $this->logDebug("Retrieve visible for Scope assignation");
        $span = $this->startSpan("Retrieve visible for  Scope assignation");
        try {
            $filter = new ScopeAssignationFilter(uids: [$ref->uid()]);
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            if ($readed = $this->readGateway->retrieve($visibleFilter)) {
                return $this->evaluatePostVisibility($readed) ? $readed : null;
            } else {
                return null;
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function retrieveVisibleForUpdate(ScopeAssignationRef $ref): ?ScopeAssignation
    {
        $this->logDebug("Retrieve visible for update for Scope assignation");
        $span = $this->startSpan("Retrieve visible for update for  Scope assignation");
        try {
            $filter = new ScopeAssignationFilter(uids: [$ref->uid()]);
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            if ($readed = $this->writeGateway->retrieveForUpdate($visibleFilter)) {
                return $this->evaluatePostVisibility($readed) ? $readed : null;
            } else {
                return null;
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function fieldsToFix(?ScopeAssignationRef $ref = null): array
    {
        $this->logDebug("Check fields to fix for Scope assignation");
        $span = $this->startSpan("Check fields for fix for Scope assignation");
        try {
            $result = $this->dispacher->dispatch(new ScopeAssignationFixedFieldsProposal(ScopeAssignation::calculatedFields(), $ref));
            return array_merge($this->fieldsToHide($ref), $result->fields);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    public function fieldsToHide(?ScopeAssignationRef $ref = null): array
    {
        $this->logDebug("Check fields to hide for Scope assignation");
        $span = $this->startSpan("Check fields to hide for  Scope assignation");
        try {
            $result = $this->dispacher->dispatch(new ScopeAssignationHideFieldsProposal([], $ref));
            return $result->fields;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    private function visiblesReferences(ScopeAssignationAttributes $attributes): ScopeAssignationAttributes
    {
        $this->logDebug("Check visibility of parent references for Scope assignation");
        $span = $this->startSpan("Check visibility of parent references for  Scope assignation");
        try {
            if ($attributes->getSecurityDomain() && !$this->securityDomainVisibilityService->checkVisibility($attributes->getSecurityDomain())) {
                throw new NotFoundException("Unknow SecurityDomain " . $attributes->getSecurityDomain());
            }
            if ($attributes->getSecurityScope() && !$this->securityScopeVisibilityService->checkVisibility($attributes->getSecurityScope())) {
                throw new NotFoundException("Unknow SecurityScope " . $attributes->getSecurityScope());
            }
            return $attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function applyPreVisibilityFilter(ScopeAssignationFilter $filter)
    {
        $this->logDebug("Compose visibility filter for Scope assignation");
        $span = $this->startSpan("Compose visibility filter for  Scope assignation");
        try {
            $result = $this->dispacher->dispatch(new ScopeAssignationFilterProposal($filter));
            return $result->scopeAssignationFilter;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function evaluatePostVisibility(ScopeAssignation $value): bool
    {
        $this->logDebug("Check if item is visible for Scope assignation");
        $span = $this->startSpan("Check if item is visible for  Scope assignation");
        try {
            $result = $this->dispacher->dispatch(new ScopeAssignationVisibilityProposal(true, $value));
            return $result->visible;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
