<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\ScopeAssignation\Application\Usecase\List;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Features\Access\ScopeAssignation\Application\Service\Visibility\ScopeAssignationVisibilityService;
use Civi\Lughauth\Features\Access\ScopeAssignation\Domain\Gateway\ScopeAssignationReadGateway;
use Civi\Lughauth\Features\Access\ScopeAssignation\Domain\Gateway\ScopeAssignationFilter;
use Civi\Lughauth\Features\Access\ScopeAssignation\Domain\Gateway\ScopeAssignationCursor;
use Civi\Lughauth\Features\Access\ScopeAssignation\Domain\Gateway\ScopeAssignationAttributesSlide;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class ScopeAssignationListUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly ScopeAssignationVisibilityService $visibility,
        private readonly ScopeAssignationReadGateway $reader
    ) {
    }

    public function allowList(): Allow
    {
        $this->logDebug("Check allow of list usecase for Scope assignation");
        $span = $this->startSpan("Check allow of list usecase for Scope assignation");
        try {
            $result = $this->dispacher->dispatch(new ScopeAssignationListAllowDecision(Allow::allowed('list', 'Allowed to list by default')));
            return $result->allow;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function count(ScopeAssignationFilter $filter): int
    {
        $this->logDebug("Run list usecase for Scope assignation");
        $span = $this->startSpan("Run list usecase for Scope assignation");
        try {
            $allow = $this->allowList();
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            return $this->visibility->countVisibles($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function list(ScopeAssignationFilter $filter, ?ScopeAssignationCursor $cursor = null): ScopeAssignationAttributesSlide
    {
        $this->logDebug("Run list usecase for Scope assignation");
        $span = $this->startSpan("Run list usecase for Scope assignation");
        try {
            $allow = $this->allowList();
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            $slide = $this->visibility->listVisibles($filter, $cursor);
            return new ScopeAssignationAttributesSlide($slide->nextCursor(), array_map(fn ($item) => $this->visibility->copyWithHidden($item->toAttributes()), $slide->values()));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
