<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\ScopeAssignation\Application\Usecase\Update;

use RuntimeException;
use Civi\Lughauth\Features\Access\ScopeAssignation\Domain\ScopeAssignation;
use Civi\Lughauth\Shared\Exception\ConstraintException;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFail;

class ScopeAssignationUpdateCheck
{
    public function __construct(public readonly ScopeAssignationUpdateParams $input, public readonly ScopeAssignation $current)
    {
    }
    public function reject(RuntimeException|ConstraintFail $fail)
    {
        if ($fail instanceof ConstraintFail) {
            throw ConstraintException::ofFail($fail);
        } else {
            throw $fail;
        }
    }
}
