<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\ScopeAssignation\Domain\Gateway;

use Civi\Lughauth\Features\Access\ScopeAssignation\Domain\ScopeAssignation;

class ScopeAssignationCursor
{
    public function __construct(
        private int $limit,
        private ?string $sinceUid = null,
    ) {
    }
    public function next(ScopeAssignation $scopeAssignation, ?int $size): ScopeAssignationCursor
    {
        $next = clone $this;
        $next->sinceUid = $scopeAssignation->uid();
        $next->limit = $size ?? $this->limit;
        return $next;
    }
    public function limit(): int
    {
        return $this->limit;
    }
    public function sinceUid(): ?string
    {
        return $this->sinceUid;
    }
}
