<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\ScopeAssignation\Domain;

use Civi\Lughauth\Features\Access\ScopeAssignation\Domain\ValueObject\Holder\ScopeAssignationUidAttributeHolder;
use Civi\Lughauth\Features\Access\ScopeAssignation\Domain\ValueObject\ScopeAssignationUidVO;
use Civi\Lughauth\Features\Access\ScopeAssignation\Domain\ValueObject\Holder\ScopeAssignationSecurityDomainAttributeHolder;
use Civi\Lughauth\Features\Access\ScopeAssignation\Domain\ValueObject\ScopeAssignationSecurityDomainVO;
use Civi\Lughauth\Features\Access\ScopeAssignation\Domain\ValueObject\Holder\ScopeAssignationSecurityScopeAttributeHolder;
use Civi\Lughauth\Features\Access\ScopeAssignation\Domain\ValueObject\ScopeAssignationSecurityScopeVO;
use Civi\Lughauth\Features\Access\ScopeAssignation\Domain\ValueObject\Holder\ScopeAssignationVersionAttributeHolder;
use Civi\Lughauth\Features\Access\ScopeAssignation\Domain\ValueObject\ScopeAssignationVersionVO;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;

class ScopeAssignationAttributes
{
    use ScopeAssignationUidAttributeHolder;
    use ScopeAssignationSecurityDomainAttributeHolder;
    use ScopeAssignationSecurityScopeAttributeHolder;
    use ScopeAssignationVersionAttributeHolder;

    private const UNSETS = [
      'uid' => 'unsetUid',
      'securityDomain' => 'unsetSecurityDomain',
      'securityScope' => 'unsetSecurityScope',
      'version' => 'unsetVersion',
    ];

    public function build(): ScopeAssignation
    {
        $errors = new ConstraintFailList();
        $uid = ScopeAssignationUidVO::tryFrom($this->uid, $errors);
        $securityDomain = ScopeAssignationSecurityDomainVO::tryFrom($this->securityDomain, $errors);
        $securityScope = ScopeAssignationSecurityScopeVO::tryFrom($this->securityScope, $errors);
        $version = ScopeAssignationVersionVO::tryFrom($this->version, $errors);
        if ($errors->hasErrors()) {
            throw $errors->asConstraintException();
        }    return new ScopeAssignation(
            uid: $uid,
            securityDomain: $securityDomain,
            securityScope: $securityScope,
            version: $version,
        );
    }
    public function unset($field)
    {
        if (isset(self::UNSETS[$field])) {
            call_user_func([$this, self::UNSETS[$field]]);
        }
    }
    public function withAssertedRules(): ScopeAssignationAttributes
    {
        $value = new ScopeAssignationAttributes();
        $errorsList = new ConstraintFailList();
        $this->withAssertedUidRules($value, $errorsList);
        $this->withAssertedSecurityDomainRules($value, $errorsList);
        $this->withAssertedSecurityScopeRules($value, $errorsList);
        $this->withAssertedVersionRules($value, $errorsList);
        if ($errorsList->hasErrors()) {
            throw $errorsList->asConstraintException();
        }
        return $value;
    }
    public function withDefaults(): ScopeAssignationAttributes
    {
        $this->withDefaultUid();
        $this->withDefaultSecurityDomain();
        $this->withDefaultSecurityScope();
        $this->withDefaultVersion();
        return $this;
    }
}
