namespace Civi\Lughauth\Features\Access\ScopeAssignation\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\ScopeAssignation\Domain\ValueObject\ScopeAssignationSecurityDomainVO;
use Civi\Lughauth\Features\Access\SecurityDomain\Domain\SecurityDomainRef;

trait ScopeAssignationSecurityDomainAccesor
{
    private ScopeAssignationSecurityDomainVO $_securityDomain;
    public function getSecurityDomain(): SecurityDomainRef
    {
        return $this->_securityDomain->value();
    }
    public function securityDomain(): ScopeAssignationSecurityDomainVO
    {
        return $this->_securityDomain;
    }
    public function isSecurityDomainChanged(?self $original = null): bool
    {
        return $this->_securityDomain->value() !== ($original ? $original->_securityDomain->value() : null);
    }
    public function withSecurityDomain(ScopeAssignationSecurityDomainVO|SecurityDomainRef $securityDomain): self
    {
        $copy = clone $this;
        $copy->_securityDomain = ScopeAssignationSecurityDomainVO::from($securityDomain);
        return $copy;
    }
}
