class ScopeAssignationSecurityScopeVO
{
    public static function from(ScopeAssignationSecurityScopeVO|SecurityScopeRef $value): ScopeAssignationSecurityScopeVO
    {
        if (is_a($value, ScopeAssignationSecurityScopeVO::class)) {
            // If is a ValueObject, its already validated
            return $value;
        } else {
            // If is not a ValueObject, validation is need and exception throw
            $errorsList = new ConstraintFailList();
            return self::tryFrom($value, $errorsList);
        }
    }
    public static function tryFrom(mixed $value, ConstraintFailList $list): ?ScopeAssignationSecurityScopeVO
    {
        if (is_a($value, ScopeAssignationSecurityScopeVO::class)) {
            // If is a ValueObject, its already validated... nothing to append
            return $value;
        } elseif (is_a($value, SecurityScopeRef::class)) {
            return new ScopeAssignationSecurityScopeVO($value);
        } else {
            $list->add(new ConstraintFail('wrong_type', ['securityScope'], [$value], ['SecurityScopeRef']));
            return null;
        }
    }
    /**
     * private constructor to avoid build a value without all the rule validations.
     */
    private function __construct(
        private readonly SecurityScopeRef $securityScope
    ) {
    }
    public function value(): SecurityScopeRef
    {
        return $this->securityScope;
    }
}
