<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\ScopeAssignation\Infrastructure\Driven;

use Override;
use Throwable;
use Civi\Lughauth\Features\Access\SecurityDomain\Domain\SecurityDomainRef;
use Civi\Lughauth\Features\Access\SecurityScope\Domain\SecurityScopeRef;
use Civi\Lughauth\Features\Access\ScopeAssignation\Domain\Gateway\ScopeAssignationReadRepository;
use Civi\Lughauth\Features\Access\ScopeAssignation\Domain\Gateway\ScopeAssignationSlide;
use Civi\Lughauth\Features\Access\ScopeAssignation\Domain\Gateway\ScopeAssignationFilter;
use Civi\Lughauth\Features\Access\ScopeAssignation\Domain\Gateway\ScopeAssignationCursor;
use Civi\Lughauth\Features\Access\ScopeAssignation\Domain\ScopeAssignationRef;
use Civi\Lughauth\Features\Access\ScopeAssignation\Domain\ScopeAssignation;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class ScopeAssignationReadRepositoryAdapter implements ScopeAssignationReadRepository
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly ScopeAssignationPdoConnector $conn,
    ) {
    }
    #[Override]
    public function resolve(ScopeAssignationRef $ref): ?ScopeAssignation
    {
        if ($ref instanceof ScopeAssignation) {
            return $ref;
        } elseif (!$ref->_private_resolve) {
            $ref->_private_resolve = $this->conn->retrieve(new ScopeAssignationFilter(uids: [ $ref->uid() ]));
        }
        return $ref->_private_resolve;
    }
    #[Override]
    public function list(?ScopeAssignationFilter $filter = null, ?ScopeAssignationCursor $sort = null): ScopeAssignationSlide
    {
        $this->logDebug("List for Scope assignation on adapter ");
        $span = $this->startSpan("List for Scope assignation on adapter");
        try {
            $values = $this->conn->list($filter, $sort);
            $last = end($values);
            return new ScopeAssignationSlide(function ($slide, $next) use ($filter) {
                return $this->list($filter, $next);
            }, new ScopeAssignationCursor($sort?->limit ?? 100, $last->uid ?? null), $values);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function retrieve(?ScopeAssignationFilter $filter): ?ScopeAssignation
    {
        $this->logDebug("Retrieve for Scope assignation on adapter ");
        $span = $this->startSpan("Retrieve for Scope assignation on adapter");
        try {
            return $this->conn->retrieve($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function exists(?ScopeAssignationFilter $filter): bool
    {
        $this->logDebug("Exists for Scope assignation on adapter ");
        $span = $this->startSpan("Exists for Scope assignation on adapter");
        try {
            return $this->conn->exists($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function count(?ScopeAssignationFilter $filter = null): int
    {
        $this->logDebug("Count for Scope assignation on adapter ");
        $span = $this->startSpan("Count for Scope assignation on adapter");
        try {
            return $this->conn->count($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function findOneByUid(string $uid): ?ScopeAssignation
    {
        $this->logDebug("Find on by uid for Scope assignation on adapter");
        $span = $this->startSpan("Find on by uid for Scope assignation on adapter");
        try {
            return $this->conn->retrieve(new ScopeAssignationFilter(uids: [$uid]));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function findOneBySecurityDomainAndSecurityScope(SecurityDomainRef $securityDomain, SecurityScopeRef $securityScope): ?ScopeAssignation
    {
        $this->logDebug("Find on by security domain security scope for Scope assignation on adapter");
        $span = $this->startSpan("Find on by security domain security scope for Scope assignation on adapter");
        try {
            return $this->conn->retrieve(new ScopeAssignationFilter(securityDomainAndSecurityScope: ScopeAssignationFilter::securityDomainAndSecurityScopeFilter(securityDomain: $securityDomain, securityScope: $securityScope)));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
