use Throwable;
use OpenApi\Attributes as OA;
use Civi\Lughauth\Features\Access\ScopeAssignation\Domain\Gateway\ScopeAssignationFilter;
use Civi\Lughauth\Features\Access\ScopeAssignation\Domain\Gateway\ScopeAssignationCursor;
use Civi\Lughauth\Features\Access\ScopeAssignation\Domain\ScopeAssignationAttributes;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;
use Civi\Lughauth\Features\Access\ScopeAssignation\Application\Usecase\List\ScopeAssignationListUsecase;
use Civi\Lughauth\Features\Access\SecurityDomain\Domain\SecurityDomainRef;
use Civi\Lughauth\Features\Access\SecurityScope\Domain\SecurityScopeRef;

class ScopeAssignationListController
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly ScopeAssignationListUsecase $listUsecase,
        private readonly ScopeAssignationRestMapper $mapper,
    ) {
    }
    #[OA\Get(
        path: "/api/access/scope-assignations",
        tags: ["Scope assignation"],
        description:"Create",
        responses: [
        new OA\Response(response: 201, description: "Ok", content: new OA\JsonContent(type: "object", ref:"#/components/schemas/ScopeAssignationApiListDTO")),
        new OA\Response(response: 422, description: "Constraint error"),
    ]
    )
    ]
    public function list(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("List Scope assignation");
        $span = $this->startSpan("List Scope assignation");
        try {
            $params = $request->getQueryParams();
            $filter = new ScopeAssignationFilter(
                uids: isset($params['uid']) ? [$params['uid']] : (isset($params['uids']) ? explode(',', $params['uids']) : null),
                search: $params['search'] ?? null,
                securityDomain: isset($params['security-domain']) ? new SecurityDomainRef($params['security-domain']) : null,
                securityDomains: isset($params['security-domains']) ? explode(",", $params['security-domains']) : null,
                securityScope: isset($params['security-scope']) ? new SecurityScopeRef($params['security-scope']) : null,
                securityScopes: isset($params['security-scopes']) ? explode(",", $params['security-scopes']) : null,
            );
            $cursor = new ScopeAssignationCursor(
                limit: (int)($params['limit'] ?? 100),
                sinceUid: $params['since-uid'] ?? null,
            );
            $result = $this->listUsecase->list($filter, $cursor);
            $value = ['items' => array_map(fn ($item) => $this->mapper->mapScopeAssignation($item), $result->values())];
            if ($nextLink = $this->nextLink($result->cursor(), $params)) {
                $value['next'] = "?{$nextLink}";
            }
            $response->getBody()->write(json_encode($value));
            return $response->withStatus(200)
              ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function nextLink(?ScopeAssignationCursor $value, array $params): ?string
    {
        $this->logDebug("Check generic allow for Scope assignation");
        $span = $this->startSpan("Generic allow for Scope assignation");
        try {
            if (!$value) {
                return null;
            }
            $link = [];
            if (isset($params['limit'])) {
                $link['limit'] = 'limit=' . $params['limit'];
            }
            if ($value && $value->sinceUid()) {
                $link['since-uid'] = 'since-uid=' . urlencode($value->sinceUid());
            }
            return implode('&', $link);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
