    public function readScopeAssignation(ServerRequestInterface $request): ScopeAssignationAttributes
    {
        $this->logDebug("Read entity for Scope assignation");
        $span = $this->startSpan("Read entity for Scope assignation");
        try {
            $body = $request->getParsedBody();
            $dto = new ScopeAssignationApiDTO();
            $dto->uid = $body['uid'] ?? null;
            $dto->securityDomain = $body['securityDomain'] ?? null;
            $dto->securityScope = $body['securityScope'] ?? null;
