        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function mapScopeAssignationApiDTO(ScopeAssignationApiDTO $body): ScopeAssignationAttributes
    {
        $this->logDebug("Map input dto to entity for Scope assignation");
        $span = $this->startSpan("Map input dto to entity for Scope assignation");
        try {
            $errorsList = new ConstraintFailList();
            $value = new ScopeAssignationAttributes();
            $value->uid(ScopeAssignationUidVO::tryFrom($body->uid ?? null, $errorsList));
            if (isset($body->securityDomain) && isset($body->securityDomain['$ref'])) {
                $value->securityDomain(ScopeAssignationSecurityDomainVO::tryFrom(new SecurityDomainRef(uid: $body->securityDomain['$ref']), $errorsList));
            }
