<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\ScopeAssignation\Infrastructure\Driver;

use Override;
use Slim\Routing\RouteCollectorProxy;
use Civi\Lughauth\Features\Access\ScopeAssignation\Infrastructure\Driver\Rest\ScopeAssignationAllowController;
use Civi\Lughauth\Features\Access\ScopeAssignation\Infrastructure\Driver\Rest\ScopeAssignationListController;
use Civi\Lughauth\Features\Access\ScopeAssignation\Infrastructure\Driver\Rest\ScopeAssignationCreateController;
use Civi\Lughauth\Features\Access\ScopeAssignation\Infrastructure\Driver\Rest\ScopeAssignationRetrieveController;
use Civi\Lughauth\Features\Access\ScopeAssignation\Infrastructure\Driver\Rest\ScopeAssignationUpdateController;
use Civi\Lughauth\Features\Access\ScopeAssignation\Infrastructure\Driver\Rest\ScopeAssignationDeleteController;
use Civi\Lughauth\Shared\Infrastructure\MicroPlugin;
use Civi\Lughauth\Shared\Event\EventListenersRegistrarInterface;
use Civi\Lughauth\Features\Access\ScopeAssignation\Application\Policy\Allow\Create\IsAutenticatedCreateAllow;
use Civi\Lughauth\Features\Access\ScopeAssignation\Application\Policy\Allow\Update\IsAutenticatedUpdateAllow;
use Civi\Lughauth\Features\Access\ScopeAssignation\Application\Policy\Allow\Retrieve\IsAutenticatedRetrieveAllow;
use Civi\Lughauth\Features\Access\ScopeAssignation\Application\Policy\Allow\List\IsAutenticatedListAllow;
use Civi\Lughauth\Features\Access\ScopeAssignation\Application\Policy\Allow\Delete\IsAutenticatedDeleteAllow;
use Civi\Lughauth\Features\Access\ScopeAssignation\Application\Usecase\Delete\ScopeAssignationDeleteAllowProposal;
use Civi\Lughauth\Features\Access\ScopeAssignation\Application\Usecase\Create\ScopeAssignationCreateAllowDecision;
use Civi\Lughauth\Features\Access\ScopeAssignation\Application\Usecase\Update\ScopeAssignationUpdateAllowDecision;
use Civi\Lughauth\Features\Access\ScopeAssignation\Application\Usecase\Retrieve\ScopeAssignationRetrieveAllowDecision;
use Civi\Lughauth\Features\Access\ScopeAssignation\Application\Usecase\List\ScopeAssignationListAllowDecision;
use Civi\Lughauth\Features\Access\ScopeAssignation\Application\Usecase\Delete\ScopeAssignationDeleteAllowDecision;

class ScopeAssignationPlugin extends MicroPlugin
{
    #[Override]
    public function registerRoutes(RouteCollectorProxy $app)
    {
        $app->group('/api/access/scope-assignations', [$this, 'setRoutesForScopeAssignation']);
        $app->group('/api/me/acl/access/scope-assignations', [$this, 'setRoutesForScopeAssignationAcl']);
    }
    #[Override]
    public function registerEvents(EventListenersRegistrarInterface $bus)
    {
        $bus->registerListener(ScopeAssignationCreateAllowDecision::class, IsAutenticatedCreateAllow::class);
        $bus->registerListener(ScopeAssignationUpdateAllowDecision::class, IsAutenticatedUpdateAllow::class);
        $bus->registerListener(ScopeAssignationRetrieveAllowDecision::class, IsAutenticatedRetrieveAllow::class);
        $bus->registerListener(ScopeAssignationListAllowDecision::class, IsAutenticatedListAllow::class);
        $bus->registerListener(ScopeAssignationDeleteAllowDecision::class, IsAutenticatedDeleteAllow::class);
    }
    public function setRoutesForScopeAssignationAcl(RouteCollectorProxy $scopeAssignationGroup)
    {
        $scopeAssignationGroup->get('', [ScopeAssignationAllowController::class, 'genericAllow']);
        $scopeAssignationGroup->get('/{uid}', [ScopeAssignationAllowController::class, 'contextualAllow']);
    }
    public function setRoutesForScopeAssignation(RouteCollectorProxy $scopeAssignationGroup)
    {
        $scopeAssignationGroup->get('', [ScopeAssignationListController::class, 'list']);
        $scopeAssignationGroup->post('', [ScopeAssignationCreateController::class, 'create']);
        $scopeAssignationGroup->get('/{uid}', [ScopeAssignationRetrieveController::class, 'retrieve']);
        $scopeAssignationGroup->put('/{uid}', [ScopeAssignationUpdateController::class, 'update']);
        $scopeAssignationGroup->delete('/{uid}', [ScopeAssignationDeleteController::class, 'delete']);
        $scopeAssignationGroup->delete('', [ScopeAssignationDeleteController::class, 'deleteAllForQuery']);
        $scopeAssignationGroup->get('/~/delete-status/{uid}', [ScopeAssignationDeleteController::class, 'checkDeleteAllForQueryStatus']);
    }
}
