class ScopeAttributePermissionVisibilityService
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly ScopeAttributePermissionReadGateway $readGateway,
        private readonly ScopeAttributePermissionWriteGateway $writeGateway,
        private readonly SecurityDomainVisibilityService $securityDomainVisibilityService,
        private readonly SecurityAttributeVisibilityService $securityAttributeVisibilityService
    ) {
    }

    public function prepareVisibleDataInList(ScopeAttributePermission $content): ScopeAttributePermissionAttributes
    {
        return $this->prepareVisibleDataCallback($content, true);
    }
    public function prepareVisibleData(ScopeAttributePermission $content): ScopeAttributePermissionAttributes
    {
        return $this->prepareVisibleDataCallback($content, false);
    }
    public function copyWithFixed(ScopeAttributePermissionAttributes $attributes, ?ScopeAttributePermission $original = null): ScopeAttributePermissionAttributes
    {
        $this->logDebug("Copy with fixed for Scope attribute permission");
        $span = $this->startSpan("Copy with fixed for  Scope attribute permission");
        try {
            $visible = $this->visiblesReferences($attributes);
            $fixed = $this->fieldsToFix($original);
            foreach ($fixed as $field) {
                $visible->unset($field);
            }
            $result = $this->dispacher->dispatch(new ScopeAttributePermissionPresetValues($attributes, $original));
            return $result->attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    public function copyWithHidden(ScopeAttributePermissionAttributes $attributes, ?ScopeAttributePermission $original = null): ScopeAttributePermissionAttributes
    {
        $this->logDebug("Copy with hidden for Scope attribute permission");
        $span = $this->startSpan("Copy with hidden for  Scope attribute permission");
        try {
            $hidden = $this->fieldsToHide($original);
            foreach ($hidden as $field) {
                $attributes->unset($field);
            }
            return $attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    public function checkVisibility(\Iterator|string|ScopeAttributePermissionRef $value): bool
    {
        $this->logDebug("Check visibility of an iterator for Scope attribute permission");
        $span = $this->startSpan("Check visibility of an iterator for  Scope attribute permission");
        try {
            if (is_a($value, \Iterator::class)) {
                $ids = [];
                foreach ($value as $val) {
                    $ids[] = $val->uid();
                }
                $filter = new ScopeAttributePermissionFilter(uids: $ids);
                return count($ids) == $this->countVisibles($filter);
            } elseif (is_a($value, ScopeAttributePermissionRef::class)) {
                return !!$this->retrieveVisibleForUpdate($value);
            } else {
                return !!$this->retrieveVisibleForUpdate(new ScopeAttributePermissionRef($value));
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function listVisibles(ScopeAttributePermissionFilter $filter, ?ScopeAttributePermissionCursor $cursor = null): ScopeAttributePermissionSlide
    {
        $this->logDebug("List visibles for Scope attribute permission");
        $span = $this->startSpan("List visibles for  Scope attribute permission");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            $result = $this->readGateway->list($visibleFilter, $cursor);
            return $result->slide(function ($item) {
                return $this->evaluatePostVisibility($item);
            });
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function countVisibles(ScopeAttributePermissionFilter $filter): int
