        $span = $this->startSpan("Count visibles for  Scope attribute permission");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            return $this->readGateway->count($visibleFilter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function listVisiblesForUpdate(ScopeAttributePermissionFilter $filter, ?ScopeAttributePermissionCursor $cursor = null): ScopeAttributePermissionSlide
    {
        $this->logDebug("List visibles for update for Scope attribute permission");
        $span = $this->startSpan("List visibles for update for  Scope attribute permission");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            $result = $this->writeGateway->listForUpdate($visibleFilter, $cursor);
            return $result->slide(function ($item) {
                return $this->evaluatePostVisibility($item);
            });
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function countVisiblesForUpdate(ScopeAttributePermissionFilter $filter): int
    {
        $this->logDebug("Count visibles for update for Scope attribute permission");
        $span = $this->startSpan("Count visibles for update for  Scope attribute permission");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            return $this->writeGateway->countForUpdate($visibleFilter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function retrieveVisible(ScopeAttributePermissionRef $ref): ?ScopeAttributePermission
    {
        $this->logDebug("Retrieve visible for Scope attribute permission");
        $span = $this->startSpan("Retrieve visible for  Scope attribute permission");
        try {
            $filter = new ScopeAttributePermissionFilter(uids: [$ref->uid()]);
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            if ($readed = $this->readGateway->retrieve($visibleFilter)) {
                return $this->evaluatePostVisibility($readed) ? $readed : null;
            } else {
                return null;
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function retrieveVisibleForUpdate(ScopeAttributePermissionRef $ref): ?ScopeAttributePermission
    {
        $this->logDebug("Retrieve visible for update for Scope attribute permission");
        $span = $this->startSpan("Retrieve visible for update for  Scope attribute permission");
        try {
            $filter = new ScopeAttributePermissionFilter(uids: [$ref->uid()]);
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            if ($readed = $this->writeGateway->retrieveForUpdate($visibleFilter)) {
                return $this->evaluatePostVisibility($readed) ? $readed : null;
            } else {
                return null;
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function fieldsToFix(?ScopeAttributePermissionRef $ref = null): array
    {
        $this->logDebug("Check fields to fix for Scope attribute permission");
        $span = $this->startSpan("Check fields for fix for Scope attribute permission");
        try {
            $result = $this->dispacher->dispatch(new ScopeAttributePermissionCollectNonEditableFields(ScopeAttributePermission::calculatedFields(), $ref));
            return array_merge($this->fieldsToHide($ref), $result->fields);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    public function fieldsToHide(?ScopeAttributePermissionRef $ref = null): array
    {
        $this->logDebug("Check fields to hide for Scope attribute permission");
        $span = $this->startSpan("Check fields to hide for  Scope attribute permission");
        try {
            $result = $this->dispacher->dispatch(new ScopeAttributePermissionCollectNonVisibleFields([], $ref));
            return $result->fields;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    private function visiblesReferences(ScopeAttributePermissionAttributes $attributes): ScopeAttributePermissionAttributes
    {
        $this->logDebug("Check visibility of parent references for Scope attribute permission");
        $span = $this->startSpan("Check visibility of parent references for  Scope attribute permission");
        try {
            if ($attributes->getSecurityDomain() && !$this->securityDomainVisibilityService->checkVisibility($attributes->getSecurityDomain())) {
                throw new NotFoundException("Unknow SecurityDomain " . $attributes->getSecurityDomain());
            }
            if ($attributes->getSecurityAttribute() && !$this->securityAttributeVisibilityService->checkVisibility($attributes->getSecurityAttribute())) {
                throw new NotFoundException("Unknow SecurityAttribute " . $attributes->getSecurityAttribute());
            }
            return $attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function applyPreVisibilityFilter(ScopeAttributePermissionFilter $filter)
    {
        $this->logDebug("Compose visibility filter for Scope attribute permission");
        $span = $this->startSpan("Compose visibility filter for  Scope attribute permission");
        try {
            $result = $this->dispacher->dispatch(new ScopeAttributePermissionRestrictFilterToVisibility($filter));
            return $result->scopeAttributePermissionFilter;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function evaluatePostVisibility(ScopeAttributePermission $value): bool
    {
        $this->logDebug("Check if item is visible for Scope attribute permission");
        $span = $this->startSpan("Check if item is visible for  Scope attribute permission");
        try {
            $result = $this->dispacher->dispatch(new ScopeAttributePermissionVisibilityCheck(true, $value));
            return $result->visible;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function prepareVisibleDataCallback(ScopeAttributePermission $content, bool $inlist): ScopeAttributePermissionAttributes
    {
        $this->logDebug("Prepare hidratation to visible data for Scope attribute permission");
        $span = $this->startSpan("Prepare hidratation to visible data for Scope attribute permission");
        try {
            $attributes = $content->toAttributes();
            $result = $this->dispacher->dispatch(new ScopeAttributePermissionEnrichForView($content, $inlist, $attributes));
            return $result->getAttributes();
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
