<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\ScopeAttributePermission\Application\Usecase\Create;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Features\Access\ScopeAttributePermission\Application\Service\Visibility\ScopeAttributePermissionVisibilityService;
use Civi\Lughauth\Features\Access\ScopeAttributePermission\Domain\ScopeAttributePermission;
use Civi\Lughauth\Features\Access\ScopeAttributePermission\Domain\Gateway\ScopeAttributePermissionWriteGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class ScopeAttributePermissionCreateUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly ScopeAttributePermissionVisibilityService $visibility,
        private readonly ScopeAttributePermissionWriteGateway $writer
    ) {
    }

    public function allowCreate(): Allow
    {
        $this->logDebug("Check allow of create usecase for Scope attribute permission");
        $span = $this->startSpan("Check allow of create usecase for Scope attribute permission");
        try {
            $result = $this->dispacher->dispatch(new ScopeAttributePermissionCreateAllowDecision(Allow::allowed('create', 'Allowed to create by default')));
            return $result->allow;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function create(ScopeAttributePermissionCreateParams $params): ScopeAttributePermissionCreateResult
    {
        $this->logDebug("Run create usecase for Scope attribute permission");
        $span = $this->startSpan("Run create usecase for Scope attribute permission");
        try {
            $allow = $this->allowCreate();
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            $this->dispacher->dispatch(new ScopeAttributePermissionCreateCheck($params));
            $enriched = $this->dispacher->dispatch(new ScopeAttributePermissionCreateEnrich($params, $params->toAttributes()));
            $attributes = $enriched->getResult();
            $input = $this->visibility->copyWithFixed($attributes);
            $entity = ScopeAttributePermission::create($input);
            $result = $this->writer->create(
                $entity,
                fn ($created) =>
                            $this->visibility->checkVisibility($created)
            );
            $output = $this->visibility->copyWithHidden($this->visibility->prepareVisibleData($result));
            return new ScopeAttributePermissionCreateResult($output);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
