<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\ScopeAttributePermission\Application\Usecase\List;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Features\Access\ScopeAttributePermission\Application\Service\Visibility\ScopeAttributePermissionVisibilityService;
use Civi\Lughauth\Features\Access\ScopeAttributePermission\Domain\Gateway\ScopeAttributePermissionReadGateway;
use Civi\Lughauth\Features\Access\ScopeAttributePermission\Domain\Gateway\ScopeAttributePermissionFilter;
use Civi\Lughauth\Features\Access\ScopeAttributePermission\Domain\Gateway\ScopeAttributePermissionCursor;
use Civi\Lughauth\Features\Access\ScopeAttributePermission\Domain\Gateway\ScopeAttributePermissionAttributesSlide;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class ScopeAttributePermissionListUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly ScopeAttributePermissionVisibilityService $visibility,
        private readonly ScopeAttributePermissionReadGateway $reader
    ) {
    }

    public function allowList(): Allow
    {
        $this->logDebug("Check allow of list usecase for Scope attribute permission");
        $span = $this->startSpan("Check allow of list usecase for Scope attribute permission");
        try {
            $result = $this->dispacher->dispatch(new ScopeAttributePermissionListAllowDecision(Allow::allowed('list', 'Allowed to list by default')));
            return $result->allow;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function count(ScopeAttributePermissionFilter $filter): int
    {
        $this->logDebug("Run list usecase for Scope attribute permission");
        $span = $this->startSpan("Run list usecase for Scope attribute permission");
        try {
            $allow = $this->allowList();
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            return $this->visibility->countVisibles($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function list(ScopeAttributePermissionFilter $filter, ?ScopeAttributePermissionCursor $cursor = null): ScopeAttributePermissionAttributesSlide
    {
        $this->logDebug("Run list usecase for Scope attribute permission");
        $span = $this->startSpan("Run list usecase for Scope attribute permission");
        try {
            $allow = $this->allowList();
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            $slide = $this->visibility->listVisibles($filter, $cursor);
            return new ScopeAttributePermissionAttributesSlide($slide->nextCursor(), array_map(fn ($item) => $this->visibility->copyWithHidden($this->visibility->prepareVisibleData($item)), $slide->values()));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
