<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\ScopeAttributePermission\Application\Usecase\Retrieve;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Features\Access\ScopeAttributePermission\Domain\ScopeAttributePermissionRef;
use Civi\Lughauth\Features\Access\ScopeAttributePermission\Application\Service\Visibility\ScopeAttributePermissionVisibilityService;
use Civi\Lughauth\Features\Access\ScopeAttributePermission\Domain\Gateway\ScopeAttributePermissionReadGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class ScopeAttributePermissionRetrieveUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly ScopeAttributePermissionVisibilityService $visibility,
        private readonly ScopeAttributePermissionReadGateway $reader
    ) {
    }

    public function allowRetrieve(?ScopeAttributePermissionRef $ref = null): Allow
    {
        $this->logDebug("Check allow of retrieve usecase for Scope attribute permission");
        $span = $this->startSpan("Check allow of retrieve usecase for Scope attribute permission");
        try {
            $result = $this->dispacher->dispatch(new ScopeAttributePermissionRetrieveAllowDecision(Allow::allowed('retrieve', 'Allowed to retrieve by default'), $ref));
            return $result->allow;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function hiddenFields(?ScopeAttributePermissionRef $ref = null): array
    {
        $this->logDebug("Check hidden fields usecase for Scope attribute permission");
        $span = $this->startSpan("Check hidden fields usecase for Scope attribute permission");
        try {
            return $this->visibility->fieldsToHide($ref);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function retrieve(string $uid): ScopeAttributePermissionRetrieveResult
    {
        $this->logDebug("Run retrieve usecase for Scope attribute permission");
        $span = $this->startSpan("Run retrieve usecase for Scope attribute permission");
        try {
            $ref = new ScopeAttributePermissionRef($uid);
            $allow = $this->allowRetrieve($ref);
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            if (!$result = $this->visibility->retrieveVisible($ref)) {
                throw new NotFoundException($uid);
            }
            $output = $this->visibility->copyWithHidden($this->visibility->prepareVisibleData($result));
            return new ScopeAttributePermissionRetrieveResult($output);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
