<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\ScopeAttributePermission\Application\Usecase\Update;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Features\Access\ScopeAttributePermission\Domain\ScopeAttributePermissionRef;
use Civi\Lughauth\Features\Access\ScopeAttributePermission\Application\Service\Visibility\ScopeAttributePermissionVisibilityService;
use Civi\Lughauth\Features\Access\ScopeAttributePermission\Domain\Gateway\ScopeAttributePermissionWriteGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class ScopeAttributePermissionUpdateUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly ScopeAttributePermissionVisibilityService $visibility,
        private readonly ScopeAttributePermissionWriteGateway $writer
    ) {
    }

    public function allowUpdate(?ScopeAttributePermissionRef $ref = null): Allow
    {
        $this->logDebug("Check allow update usecase for Scope attribute permission");
        $span = $this->startSpan("Check allow update usecase for Scope attribute permission");
        try {
            $result = $this->dispacher->dispatch(new ScopeAttributePermissionUpdateAllowDecision(Allow::allowed('update', 'Allowed to update by default'), $ref));
            return $result->allow;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function fixedFields(?ScopeAttributePermissionRef $ref = null): array
    {
        $this->logDebug("Check fixed fields for update usecase for Scope attribute permission");
        $span = $this->startSpan("Check fixed fields for update usecase for Scope attribute permission");
        try {
            return $this->visibility->fieldsToFix($ref);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function update(string $uid, ScopeAttributePermissionUpdateParams $params): ScopeAttributePermissionUpdateResult
    {
        $this->logDebug("Run update usecase for Scope attribute permission");
        $span = $this->startSpan("Run update usecase for Scope attribute permission");
        try {
            $ref = new ScopeAttributePermissionRef($uid);
            $allow = $this->allowUpdate($ref);
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            if (!$original = $this->visibility->retrieveVisibleForUpdate($ref)) {
                throw new NotFoundException($uid);
            }
            $this->dispacher->dispatch(new ScopeAttributePermissionUpdateCheck($params, $original));
            $enriched = $this->dispacher->dispatch(new ScopeAttributePermissionUpdateEnrich($params, $original, $params->toAttributes()));
            $attributes = $enriched->getResult();
            $input = $this->visibility->copyWithFixed($attributes);
            $modified = $original->replace($input);
            $result = $this->writer->update($original, $modified);
            $output = $this->visibility->copyWithHidden($this->visibility->prepareVisibleData($result));
            return new ScopeAttributePermissionUpdateResult($output);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
