<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\ScopeAttributePermission\Domain\Gateway;

use Civi\Lughauth\Features\Access\ScopeAttributePermission\Domain\ScopeAttributePermission;

class ScopeAttributePermissionCursor
{
    public function __construct(
        private int $limit,
        private ?string $sinceUid = null,
    ) {
    }
    public function next(ScopeAttributePermission $scopeAttributePermission, ?int $size): ScopeAttributePermissionCursor
    {
        $next = clone $this;
        $next->sinceUid = $scopeAttributePermission->uid();
        $next->limit = $size ?? $this->limit;
        return $next;
    }
    public function limit(): int
    {
        return $this->limit;
    }
    public function sinceUid(): ?string
    {
        return $this->sinceUid;
    }
}
