<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\ScopeAttributePermission\Domain\ValueObject;

use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFail;

class ScopeAttributePermissionWriteVO
{
    public static function from(ScopeAttributePermissionWriteVO|bool $value): ScopeAttributePermissionWriteVO
    {
        if (is_a($value, ScopeAttributePermissionWriteVO::class)) {
            // If is a ValueObject, its already validated
            return $value;
        } else {
            // If is not a ValueObject, validation is need and exception throw
            $errorsList = new ConstraintFailList();
            return self::tryFrom($value, $errorsList);
        }
    }
    public static function tryFrom(mixed $value, ConstraintFailList $list): ?ScopeAttributePermissionWriteVO
    {
        if (is_a($value, ScopeAttributePermissionWriteVO::class)) {
            // If is a ValueObject, its already validated... nothing to append
            return $value;
        } elseif (is_bool($value)) {
            return new ScopeAttributePermissionWriteVO($value);
        } else {
            $list->add(new ConstraintFail('wrong_type', ['write'], [$value], ['bool']));
            return null;
        }
    }
    /**
     * private constructor to avoid build a value without all the rule validations.
     */
    private function __construct(
        private readonly bool $write
    ) {
    }
    public function value(): bool
    {
        return $this->write;
    }
}
