    }
    #[Override]
    public function resolve(ScopeAttributePermissionRef $ref): ?ScopeAttributePermission
    {
        if ($ref instanceof ScopeAttributePermission) {
            return $ref;
        } elseif (!$ref->_private_resolve) {
            $ref->_private_resolve = $this->conn->retrieve(new ScopeAttributePermissionFilter(uids: [ $ref->uid() ]));
        }
        return $ref->_private_resolve;
    }
    #[Override]
    public function list(?ScopeAttributePermissionFilter $filter = null, ?ScopeAttributePermissionCursor $sort = null): ScopeAttributePermissionSlide
    {
        $this->logDebug("List for Scope attribute permission on adapter ");
        $span = $this->startSpan("List for Scope attribute permission on adapter");
        try {
            $values = $this->conn->list($filter, $sort);
            $last = end($values);
            return new ScopeAttributePermissionSlide(function ($slide, $next) use ($filter) {
                return $this->list($filter, $next);
            }, new ScopeAttributePermissionCursor($sort?->limit ?? 100, $last->uid ?? null), $values);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function retrieve(?ScopeAttributePermissionFilter $filter): ?ScopeAttributePermission
