    public function findOneBySecurityDomainAndSecurityAttribute(SecurityDomainRef $securityDomain, SecurityAttributeRef $securityAttribute): ?ScopeAttributePermission
    {
        $this->logDebug("Find on by security domain security attribute for Scope attribute permission on adapter");
        $span = $this->startSpan("Find on by security domain security attribute for Scope attribute permission on adapter");
        try {
            return $this->conn->retrieve(new ScopeAttributePermissionFilter(securityDomainAndSecurityAttribute: ScopeAttributePermissionFilter::securityDomainAndSecurityAttributeFilter(securityDomain: $securityDomain, securityAttribute: $securityAttribute)));
        } catch (Throwable $ex) {
